import { useEffect } from "react";
import Card from "./Card"

const Cards = ({courses, filter}) => {
  let allCourses= [];
  const [currCourses, setCurrCourses]= useState(allCourses);
  const getCourses= ()=>{
    Object.values(courses).forEach(courseCat => {
      courseCat.forEach((course)=>{
        allCourses.push(course);
      });
    });
  }
  
  useEffect( ()=>{
    if(filter===1){
      setCurrCourses(allCourses);
    }
    else{
      currCourses= Object.values(courses)[filter-1];
    }
  }, [filter]);

  getCourses();
  return (
    <div className="flex flex-wrap justify-center gap-4 mb-4">      
      {
      currCourses.map((course)=><Card course= {course} />
      )
    }
    </div>
  )
}

export default Cards
