import { useEffect, useState } from "react";
import Card from "./Card"

const Cards = ({courses, filter}) => {
  const [allCourses, setAllCourses]= useState([]);
  const [currCourses, setCurrCourses]= useState(allCourses);
  
  useEffect(()=>{
    let newArr= [];
    const getCourses= ()=>{
      Object.values(courses).forEach(courseCat => {
        courseCat.forEach((course)=>{
          newArr.push(course);
        });
      });
    }
    getCourses();
    setAllCourses(newArr);
  }, []);
  
  
  useEffect( ()=>{
    console.log(allCourses);
    if(filter==1){
      setCurrCourses(allCourses);
    }
    else{
      setCurrCourses(Object.values(courses)[filter-2]);
    }
  }, [filter]);

  return (
    <div className="flex flex-wrap justify-center gap-4 mb-4">      
      {
      currCourses.map((course)=><Card course= {course} />
      )
    }
    </div>
  )
}

export default Cards
