import { useEffect, useState } from "react";
import Card from "./Card"

const Cards = ({ courses, filter }) => {
  let allCourses = [];

  useEffect(() => {
    const getCourses = () => {
      Object.values(courses).forEach(courseCat => {
        courseCat.forEach((course) => {
          allCourses.push(course);
        });
      });
    }
    getCourses();
  }, []);

  const [currCourses, setCurrCourses] = useState(allCourses);
  useEffect(() => {
    if (filter == 1) {
      setCurrCourses(allCourses);
    }
    else {
      setCurrCourses(Object.values(courses)[filter - 2]);
    }
  }, [filter]);

  
}

export default Cards
